import { Weather } from '../dto/dto';
import { Entity, Schema, Client } from 'redis-om'
import { WeatherError } from '../errors/weatherError';
import { errorCodes } from '../errors/errorStatusCode';
import weatherRepository from './repository/weather.repository';



class WeatherEntity extends Entity { };

class WeatherDao {

    schema = new Schema(WeatherEntity, {
        cityId: { type: 'number' },
        temperature: { type: 'number' },
        date: { type: 'date', sortable: true }
    });



    public async addWeather(weather: Weather) {
        return (await weatherRepository).createAndSave(weather);
    }
    public async getWeather(cityId: number) {

        const weatherData = (await weatherRepository).search()
            .where('cityId').eq(cityId)
            .returnMax('date');
        return weatherData;
    }
    public async getAllWeatherForCity(cityId: number) {

        const weatherData = (await weatherRepository).search()
            .where('cityId').eq(cityId)
            .return.all();
        return weatherData;
    }
    public async getAllWeather() {

        const weatherData = (await weatherRepository).search()
            .return.all();
        return weatherData;
    }
    public async getWeatherForAllCities() {
        try {
            return (await weatherRepository).search().return.all()
        } catch (err) {
            throw new WeatherError(`Error getting all weather data from REDIS`, errorCodes.REDIS_SEARCH_ERROR);
        }
    }




}

export default new WeatherDao();

import { errorCodes } from './errorStatusCode';


const weatherLogger = console;

export class WeatherError extends Error {
    statusCode = 200;
    constructor(message: string, status?: number) {
        super(message);
        Object.setPrototypeOf(this, WeatherError);
        if (typeof status !== "undefined") this.statusCode = status;

        switch (status) {
            case errorCodes.REDIS_CONNECTION_ERROR: {
                weatherLogger.error(`${new Date()} Error connecting to REDIS database:`, message);
                break;
            }
            case errorCodes.WEATHERBIT_COMMUNICATION_ERROR: {
                weatherLogger.error(`${new Date()} Error getting data from weatherbits:`, message);
                break;
            }
            case errorCodes.REDIS_SEARCH_ERROR: {
                weatherLogger.error(`${new Date()} Could not get data from REDIS:`, message);
                break;
            }
            case errorCodes.OTHER_ERROR: {
                weatherLogger.error(`${new Date()} Another application error:`, message);
                break;
            }
            default: {
                weatherLogger.error(`${new Date()} Unexpected error:`, message);
            }


        }

    }

    getErrorMessage(): string {
        return `Something went wrong ${this.message}`;
    }

}

